import { Injectable } from '@angular/core';
import Product from "../model/Product";

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  produit1: Product = new Product('1', 150, 'telephone','decs',false,'data:image/jpeg;base64,');
  produit2: Product = new Product('2', 500, 'ordinateur','decs',true,'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7ercXF2Ej1mDvRBIqe3wdWr6-DxrIYfecfCa7nyE1g_DL3Apo&s');
  produit3: Product = new Product('3', 700, 'tablet','decs',false,'data:image/jpeg;base64,');
  listproduct: Array<Product> = [this.produit1, this.produit2, this.produit3];
  constructor() { }
  add(product: Product):void{
     this.listproduct.push(product);
  }
  get(id: string): Product{
    return this.listproduct.find((p)  => {return p.code === id;});
  }
  getAll(): Array<Product> {
    return this.listproduct;
  }

}
